				$gFile =  '../sitemap/sitemap' . $gnumber .'.xml';
				echo '<br/>'.$gFile.'<br/>';
				
				$put_str = $mapPre . $str . $mapEnd;
				@unlink($gFile);
				file_put_contents($gFile,$put_str);

				$str = '';
				$gnumber++;
				

			}
			
			if($starPri >= 400 && $priority != 0.1){
				$starPri = 0;
				$priority = $priority - 0.1;
			}
			
			if($priority > 0.1){
				
				$str .= "     <url>
					 <loc>" . $url . "</loc> 
					 <lastmod>". $date . "T" . $time ."-05:00</lastmod>   
					 <changefreq>daily</changefreq> 
					 <priority>". $priority . "</priority> 
					 </url>
				";	
			}else{
										$str .= "     <url>
			 <loc>" . $url . "</loc> 
			 <lastmod>". $date . "T" . $time ."-05:00</lastmod>   
			 <changefreq>daily</changefreq> 
			 <priority>0.1</priority> 
			 </url>
		";	
			}
			
			$star++;
			$starPri++;
		}

	}
	
	{
		$gFile =  '../sitemap/sitemap' . $gnumber .'.xml';
		echo '<br/>'.$gFile.'<br/>';
		
		$gnumber++;

		$put_str = $mapPre . $str . $mapEnd;
		@unlink($gFile);
		file_put_contents($gFile,$put_str);
	
	}
	
	unset($tempArr1);
	unset($filenames);
	echo "生成sitemap成功！";	
}




